mapper.select:
select num,subject, writer,input_date,ip from (select num,subject, writer,input_date,ip, row_number() over( order by input_date desc) rnum from m_board
instr(subject,#{keyword}) != 0
instr(content,#{keyword}) != 0
instr(writer,#{keyword}) != 0
)where rnum between #{startNum} and #{endNum}

mapper.update:
update m_board


		subject=#{subject},


		content=#{content}


where num=#{num} and writer=#{writer} and pass=#{pass}

mapper.delete:
delete from m_board
where
num=#{num} and writer=#{writer} and pass=#{pass}
